mapper.insert:

    select max(id + 1) from tb_board


insert into tb_board ( id
                     , title
                     , contents
                     , writer)
values ( #{id}
       , #{title}
       , #{contents}
       , #{writer})

mapper.update:
update tb_board
set title    = #{title}
  , contents = #{contents}
  , writer = #{writer}
where id = #{id}
